import React,{Component} from "react";
var myHeaders = new Headers();
var raw = "{\n    \"EmailAddress\" :\"moalighaliany@gmail.com\",\n    \"Password\" : \"12345678\"\n}";

var requestOptions = {
  method: 'GET',
  body: raw,
  redirect: 'follow'
};
class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      Yourtoken: []
    };
  }

  componentDidMount() {


    fetch("https://barfas-server.herokuapp.com/login",requestOptions)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            Yourtoken: result.Yourtoken
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, Yourtoken } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {Yourtoken}

        </ul>
      );
    }
  }
}


export default MyComponent;
