var axios = require('axios');
var data = JSON.stringify({
  "phone": "9367893533",
  "password": "a1234567"
});

var config = {
  method: 'post',
  url: 'https://foodzila.iran.liara.run/users/api?login',
  headers: { 
    'Content-Type': 'application/json'
  },
  data : data
};
class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      YourToken: []
    };
  }

  componentDidMount() {
    axios(config)
    .then(function (response) {
      this.setState({
        YourToken: response.YourToken
      });
    })
    .catch(function (error) {
      console.log(error);
    });

  }

  render() {
    const { YourToken } = this.state;

      return (
        <ul>
          {YourToken}

        </ul>
      );

  }
}


export default MyComponent;
