import React,{Component} from "react";

var raw = JSON.stringify({
  "jsonrpc": "2.0",
  "method": "account_history_api.get_account_history",
  "params": {
    "account": "steemit",
    "start": 1000,
    "limit": 1000
  },
  "id": 1
});

var requestOptions = {
  method: 'POST',
  body: raw,
  redirect: 'follow'
};

class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      ans: []
    };
  }

  componentDidMount() {


     fetch("https://api.steemit.com",requestOptions)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            ans: result.result.history[0][0].block
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, ans } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div></div>;
    } else {
      return (
        <ul>
          {ans}

        </ul>
      );
    }
  }
}


export default MyComponent;
