import React,{Component} from "react";

function handleSubmit(e) {
  e.preventDefault()
  const {Name} = e.target.elements
  return {Name}

}


var raw = JSON.stringify({
  "jsonrpc": "2.0",
  "method": "account_history_api.get_account_history",
  "params": {
    "account": {Name},
    "start": 1000,
    "limit": 1000
  },
  "id": 1
});

var requestOptions = {
  method: 'POST',
  body: raw,
  redirect: 'follow'
};

class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      ans: []
    };
  }


  componentDidMount() {


     fetch("https://api.steemit.com",requestOptions)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            ans: result.result.history[0][1].block
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }



  render() {
    const { error, isLoaded, ans } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div></div>;
    } else {
      return (
        <div>
          <form/>

        <div>
          {ans}
        </div>
        </div>
      );
    }
  }
}


export default MyComponent;
