      "method": "account_history_api.get_account_history",
      "params": {
        "account": {value},
        "start": 1000,
        "limit": 1000
      },
      "id": 1
    });

    var requestOptions = {
      method: 'POST',
      body: raw,
      redirect: 'follow'
    };


     fetch("https://api.steemit.com",requestOptions)
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            ans: result.result.history[0][1].block
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }



  render() {
    const { error, isLoaded, ans } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div></div>;
    } else {
      return (
        <div>
         <FORM />

        <div>
          {ans}
        </div>
        </div>
      );
    }
  }
}


export default MyComponent;
