import React,{Component} from "react";
var axios = require('axios');
var data = JSON.stringify({
  "phone": "9367893533",
  "password": "a1234567"
});

var config = {
  method: 'post',
  url: 'https://foodzila.iran.liara.run/users/api?login',
  headers: { 
    'Content-Type': 'application/json'
  },
  data : data
};
class MyComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Name: []
    };
  }

  componentDidMount() {
    axios(config)
    .then(function (response) {
      this.setState({
        Name: response[0].YourToken
      });
    })
    .catch(function (error) {
      console.log(error);
    });

  }

  render() {
    const { Name } = this.state;
    if (error) {
      return <div></div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {Name}

        </ul>
      );
    }
  }
}


export default MyComponent;
